from langflow.custom import Component
from langflow.template import Input, Output
from langflow.schema import Data
from typing import List, Dict, Any, Callable
import json

class ProcessImages(Component):
    display_name = "ProcessImages"
    description = "Handles image generation and uploading for React component creation"
    icon = "image"
    name = "ProcessImages"

    inputs = [
        Input(
            name="imggen_callable",
            display_name="ImgGen Callable",
            field_type="function",
            input_types=["Callable"],
            required=True,
        ),
        Input(
            name="uploader_callable",
            display_name="Uploader Callable",
            field_type="function",
            input_types=["Callable"],
            required=True,
        ),
        Input(
            name="required_images",
            display_name="Required Images",
            field_type="List[Dict]",
            input_types=["list"],
        ),
    ]

    outputs = [
        Output(
            name="Results Dict",
            display_name="Results Dict", 
            method="process_images",
        ),
        Output(
            display_name="Process Images Callable",
            name="Process Images Callable",
            method="get_process_images_callable",
        ),
    ]

    def process_images(self,required_images: List[Dict] = None) -> Data:
        """
        Process image generation requests and return results in required format.
        """
        if required_images: self.required_images = required_images
        imggen_callable = self.imggen_callable
        uploader_callable = self.uploader_callable

        if isinstance(required_images, str):
            try:
                required_images = json.loads(required_images)
            except json.JSONDecodeError:
                return Data(data=[{
                    "error": "Invalid JSON format for required_images"
                }])

        results = []

        for image_req in required_images:
            try:
                prompt = image_req.get("generation_prompt")
                target = image_req.get("target_component")

                dimensions = image_req.get("suggested_dimensions", {})
                if not isinstance(dimensions, dict):
                    dimensions = {}

                if not prompt or not target:
                    results.append({
                        "target_component": target or "unknown",
                        "link": "Error: Missing required fields",
                        "error": "Missing generation_prompt or target_component"
                    })
                    continue

                width = int(dimensions.get("width", 512))
                height = int(dimensions.get("height", 512))

                enhanced_prompt = prompt

                base64_image = imggen_callable(
                    prompt=enhanced_prompt,
                    width=width,
                    height=height
                )

                if not base64_image:
                    raise ValueError("Image generation failed")

                uploaded_url = uploader_callable(base64_image)

                results.append({
                    "target_component": target,
                    "link": uploaded_url,
                    "dimensions": {
                        "width": width,
                        "height": height
                    },
                })

            except Exception as e:
                results.append({
                    "target_component": image_req.get("target_component", "unknown"),
                    "link": f"Error: {str(e)}",
                    "error": str(e)
                })

        return results

    def get_process_images_callable(self) -> Callable:
        return self.process_images
